package swt_test

import (
	"fmt"
	"github.com/SecureWebhookToken/swt"
	"github.com/stretchr/testify/assert"
	"io"
	"net/http"
	"testing"
)

func TestBuildRequest(t *testing.T) {
	const (
		dataImgUrl = "data:image/jpeg;base64,"
		url        = "http://swt-test.com"
	)

	type wantArgs struct {
		Method      string
		URL         string
		Body        []byte
		ContentType string
	}

	tests := []struct {
		name    string
		url     string
		issuer  string
		event   string
		data    []byte
		key     any
		want    wantArgs
		wantErr error
	}{
		{
			name:   "Build valid webhook HEAD request",
			url:    url,
			issuer: "me",
			event:  "user.create",
			data:   nil,
			key:    secretKey,
			want:   wantArgs{Method: http.MethodHead, URL: url, Body: nil},
		},
		{
			name:   "Build valid webhook HEAD request",
			url:    url,
			issuer: "me",
			event:  "user.create",
			data:   []byte(`{"username":"me"}`),
			key:    secretKey,
			want: wantArgs{
				Method:      http.MethodHead,
				URL:         url,
				Body:        nil,
				ContentType: "application/json",
			},
		},
		{
			name:   "Build valid webhook POST request",
			url:    url,
			issuer: "me",
			event:  "user.create",
			data:   []byte(fmt.Sprintf(`{"username":"me","image":"%s"}`, dataImgUrl)),
			key:    secretKey,
			want: wantArgs{
				Method:      http.MethodPost,
				URL:         url,
				Body:        []byte(fmt.Sprintf(`{"username":"me","image":"%s"}`, dataImgUrl)),
				ContentType: "application/json",
			},
		},
		{
			name:    "Build invalid webhook HEAD request, due to invalid json",
			url:     url,
			issuer:  "me",
			event:   "user.create",
			data:    []byte(`{"username:"test"}`),
			key:     secretKey,
			wantErr: swt.ErrInvalidJSON,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := swt.BuildRequest(tt.url, tt.issuer, tt.event, tt.data, tt.key)
			if err != nil {
				assert.ErrorIs(t, err, tt.wantErr)
				return
			}
			assert.Equal(t, tt.want.Method, got.Method)
			assert.Equal(t, tt.want.URL, got.URL.String())

			if got.Method == http.MethodPost {
				body, err := io.ReadAll(got.Body)
				assert.NoError(t, err)
				assert.Equal(t, tt.want.Body, body)
				assert.Equal(t, tt.want.ContentType, got.Header.Get("Content-Type"))
			}
		})
	}
}
